import React from 'react';

import './product-grid-item.scss';
import 'hover.css'
import CustomButton from "../custom-button/custom-button";
import Img from 'react-image';
import {addToCart, removeFromCart,unLoadCart} from "../../redux/cart/cart.actions";
import {connect} from "react-redux";

const ProductGridItem = ({product,addToCart,cart,unLoadCart}) => {
    unLoadCart();
    const {name,image,price,_id:itemId}  = product;
    console.log(cart.id)
    return <div className={`product-grid-item`} onClick={()=>removeFromCart(cart.id,{name,image,price,itemId})}>

            <Img className={`product-grid-item-image `} src={`${image}`} alt=""/>

        <CustomButton onClick={()=>addToCart(cart.id,{name,image,price,itemId})} customStyle={`product-grid-item-cart`} btnText={'Add to cart'}/>
        <span  className={`product-grid-item-price`}>${`${price}`}</span>
    </div>
};
const mapStateToProps = state=>{

    return{
        cart:state.cart
    }

};

export default connect(mapStateToProps,{addToCart,removeFromCart})(ProductGridItem);
