import React, {Component} from 'react';
import CartItem from "./cartItem/cartItem.component";
import {connect} from "react-redux";
import './cart.style.scss'
import {addToCart, deleteFromCart, removeFromCart} from "../../redux/cart/cart.actions";


class Cart extends React.Component{

    cartOperation = (op,item)=>e=>{
      if (op==='remove'){
          removeFromCart(item);
      }
      if (op==='delete'){
          deleteFromCart(item);
      }
      if (op==='add'){
          addToCart(item)
      }
    };

    render() {

        return (
            <div className={`cart`}>
                {this.props.cart.items.map(item =>{

                    return <CartItem  cartOperation={this.cartOperation} product={item} key={item._id}/>;
                })}
            </div>
        );
    }
}

