import {FETCH_PRODUCTS} from "../action-types";
// import appData from "../../api/appData";
import axios from 'axios'


export const fetchProducts = (type, item) => async dispatch => {
    const response = await axios.get(`https://cors-anywhere.herokuapp.com/https://noctem-a60b5.firebaseio.com/products/`);
    console.log(response.data)
    dispatch({
        type: FETCH_PRODUCTS,
        payload: response.data[type][item]
    })
};
