const Cart = ({cart}) => {

    const totalPrice = cart.items.map(item=>item.itemCount*item.price).reduce((a,c)=>a+c);

    return (
        <div className={`container-fluid cart`}>
                {cart.items.map(item=>{
                    return <CartItem key={item.id} product={item}/>
                })}
                <div className={`totalPrice`}>
                    <span className={`totalPrice-divider`}>-</span>
                    <span className={`totalPrice-text`}>{totalPrice}</span>
                </div>
            <CustomButton customStyle={`cart-clear`}>Clear All</CustomButton>
            <CustomButton customStyle={`cart-checkout`}>Check Out </CustomButton>
            </div>
    );
};



const mapStateToProps = state=>{
  return{
      cart:state.cart
  }
};
export default connect(mapStateToProps)(Cart);
