import React from 'react';
import './cart-item.scss';
import Img from 'react-image';
import {addToCart,removeFromCart} from "../../redux/cart/cart.actions";
import {connect} from "react-redux";

const CartItem = ({image,name,price,itemCount,itemId,addToCart,removeFromCart,cart}) => {
    return (
        <div className={`cart-item`}>
            <Img src={image} className={`cart-item-img`}/>
            <div className={`cart-item-info`}>
                <h3 className={`cart-item-info-name`}>{name}</h3>
                <span className={`cart-item-info-price`}>{price}</span>
                <div className={`cart-item-info-quantity`}>
                    <span onClick={()=>removeFromCart(cart.id,itemId)}>&larr;</span>
                    <span>{itemCount}</span>
                    <span onClick={()=>addToCart(cart.id,itemId)} >&rarr;</span>
                </div>
            </div>

            <span>remove </span>
        </div>
    );
};
const mapStateToProps = state =>{
    return{
        cart:state.cart
    }
};
export default connect(mapStateToProps,{addToCart,removeFromCart})(CartItem);
