import React from 'react';
import './navbar.style.scss';
import {Link} from "react-router-dom";
import {connect} from "react-redux";
import {signOut} from "../../redux/signIn/signIn.actions";
import appData from "../../api/appData";
import CustomButton from "../custom-button/customButton.component";


class Navbar extends React.Component {

    state={
        visible:false,

    };
    handleClick=()=>{

        if (!this.state.visible){
            document.addEventListener('click', this.handleOutsideNavbar, false);
        }else {
            document.removeEventListener('click', this.handleOutsideNavbar, false);
        }
        this.setState(prevState => ({
            visible: !prevState.visible,
        }));
    };
    handleOutsideNavbar=(e)=> {
        if (this.node.contains(e.target)) {
            return;
        }

        this.handleClick();
    };

    render() {
        return (
            <div className={`nav`} ref={node => { this.node = node; }}>
                <Link className={`nav-brand`} to={`/`}>NOCTEM</Link>
                <div className={`nav-menu-desktop`}>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>MEN</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>WOMEN</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>GIRLS</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>BOYS</Link>
                </div>
                <div className={`nav-menu`}>
                    <Link className={`nav-menu-item`} to={'/products/men/:item'}>SEARCH</Link>
                    <Link className={`nav-menu-item`} to={'/products/men/:item'}>SIGN IN</Link>
                    <Link className={`nav-menu-item`} to={'/products/men/:item'}>CART</Link>
                    <CustomButton customStyle={'nav-toggle'} onClick={this.handleClick}>{this.state.visible? <span> &#10094;</span>:<span>&#10095;</span>}</CustomButton>
                </div>

                <div className={`nav-expand`} style={{display:this.state.visible?'flex':'none'}}>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>MEN</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>WOMEN</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>GIRLS</Link>
                    <Link className={`nav-expand-item`} to={'/products/men/:item'}>BOYS</Link>
                </div>
            </div>
        );
    }
}

const mapStateToProps = state=>{
  return{
      isSignedIn:state.signIn.isSignedIn,
      cart:state.cart
  }
};

export default connect(mapStateToProps,{signOut})(Navbar);
