import appData from "../../api/appData";
import {SIGN_UP_USER} from "../action-types";

export const signUpUser = (formValues)=> async dispatch=>{

    const response = await appData.post(`/users/`,formValues);
    dispatch({
        type:SIGN_UP_USER,
        payload:response.data
    })
};
