import React from "react";

import {Field, reduxForm} from "redux-form";
import FormInput from "../form-input/form-input";
import './sign-in-form.scss'
import CustomButton from "../custom-button/custom-button";
import {connect} from "react-redux";
import {fetchUser} from "../../redux/user/user-action";
import {signIn} from "../../redux/auth/auth-actions";
import {Link} from "react-router-dom";

class SignInForm extends React.Component {

    onSubmit = ({email,password}) => {
        this.props.fetchUser(email,password);
        if (this.props.user){
            this.props.signIn(email);
            this.props.history.push('/');
        }

    };

    render() {
        console.log(this.props)
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className={`sign-in-form`}>
                <Field name="email" component={FormInput} label={`EMAIL`} placeholder={`enter your email`}/>
                <Field name="password" component={FormInput} label={`PASSWORD`} placholder={`enter your password`}/>
                <CustomButton btnText={'Log In'}/>

                <span>Dont Have an account ? <Link to={'/signup'}>Sign up</Link></span>
            </form>

        );
    }
}

const validate = formValues => {
    const errors = {};
    if (!formValues.email) {
        errors.email = 'You must enter an email'
    }
    if (!formValues.password) {
        errors.password = 'You must enter a valid password';
    }
    return errors;
};
const mapStateToProps = state =>{
    return{
        user:state.user
    }
};

const SignInFormRedux= connect(mapStateToProps,{fetchUser,signIn})(SignInForm);

export default reduxForm(

    {
        form: "signIn",
        validate
    }
)(SignInFormRedux);
