const Cart = ({cart}) => {

    const totalPrice = cart.items.map(item=>item.itemCount*item.price).reduce((a,c)=>a+c);

    return (
        <div className={`cart`}>
                {cart.items.map(item=>{
                    return <CartItem key={item.id} product={item}/>
                })}
            {totalPrice}
            </div>
    );
};



const mapStateToProps = state=>{
  return{
      cart:state.cart
  }
};
export default connect(mapStateToProps)(Cart);
