import React, {Component} from 'react';
import {Route, Switch} from 'react-router-dom';
import './App.css';
import Homepage from "./pages/homepage/homepage";
import Navbar from "./components/navbar/navbar";
import SignInForm from  './components/sign-in-form/sign-in-form';
import Products from "./pages/Products/Products";
import SignUpForm from './components/sign-up-form/sign-up-form'
import Cart from "./components/cart/cart";
import appData from "./api/appData";
import {connect} from "react-redux";


class App extends Component {


   componentWillUnmount() {
        window.addEventListener('beforeunload',this.saveCartData);
        window.removeEventListener('beforeunload',this.saveCartData);
   }
   saveCartData = async ()=>{
       if (this.props.signIn.isSignedIn){
           await appData.post('/cart/update',this.props.cart.items);
       }
   };

    render() {
        console.log(localStorage)
        return (
            <div>
                <div>
                    <Navbar/>
                    <Switch>
                        <Route path={'/'} exact component={Homepage}/>
                        <Route path={'/products/:category/:type'} component={Products}/>
                        <Route path={`/signin`} component={SignInForm}/>
                        <Route path={`/signup`} component={SignUpForm}/>
                        <Route path={`/cart`} component={Cart}/>
                    </Switch>
                </div>
            </div>
        );
    }
}
const mapStateToProps = state=>{
  return{
      cart:state.cart,
      signIn:state.signIn
  }
};
export default connect(mapStateToProps)(App);
