import React, {Component} from 'react';
import {Route, Switch} from 'react-router-dom';
import './App.css';
import Homepage from "./pages/homepage/homepage";
import Navbar from "./components/navbar/navbar.component";
import SignInForm from './components/signInForm/signInForm.component';
import Products from "./pages/Products/Products";
import SignUpForm from './components/signUpFrom/signUpForm.component'
import Cart from "./components/cart/cart.component";

import {connect} from "react-redux";
import appData from "./api/appData";


class App extends Component {
    componentDidMount() {
        window.addEventListener('beforeunload',async (e)=>{
            e.preventDefault();
            if (this.props.isSignedIn){
                const {email,items} = this.props.cart;
                const body = JSON.stringify({email,items});
                const config = {
                    headers:{
                        "Content-Type":"application/json"
                    }
                };
                const res = await appData.post('/cart/update',body,config);
            }
            return undefined;
        });

    }

    render() {
        console.log(localStorage);
        return (
            <div>
                <div>
                    <Navbar/>
                    <Switch>
                        <Route path={'/'} exact component={Homepage}/>
                        <Route path={'/products/:category/:type'} component={Products}/>
                        <Route path={`/signin`} component={SignInForm}/>
                        <Route path={`/signup`} component={SignUpForm}/>
                        <Route path={`/cart`} component={Cart}/>
                    </Switch>
                </div>
            </div>
        );
    }
}
const mapStateToProps = state=>{
  return{
      cart:state.cart,
      signIn:state.signIn
  }
};
export default connect(mapStateToProps)(App);
