export const addToCart = (items,targetItem)=>{
    const targetItemId = targetItem._id;
    const sameItemIndex = items.findIndex(item => item._id ===targetItemId );
    if (sameItemIndex>-1)
        items[sameItemIndex].itemCount+=1;
    else
        items.push(targetItem);

   return items;
};

