import React from 'react';

import './productGridItem.style.scss';
import 'hover.css'
import CustomButton from "../../custom-button/customButton.component";
import Img from 'react-image';
import {addToCart, removeFromCart} from "../../../redux/cart/cart.actions";
import {connect} from "react-redux";

const ProductGridItem = ({product,addToCart,cart}) => {

    const {name,image,price}  = product;

    return <div className={`item`}>
        <Img src={image} className={`item-image`}/>
        <div className={`item-info`}>
            <span className={`item-info-name`}>{name}</span>
            <span className={`item-info-price`}>{price}</span>
        </div>
    </div>
};
const mapStateToProps = state=>{
    return{
        cart:state.cart
    }
};

export default connect(mapStateToProps,{addToCart})(ProductGridItem);
