import appData from "../../api/appData";
import {
    SIGN_IN_SUCCESS,
    SIGN_IN_FAIL, SIGN_UP_SUCCESS, SIGN_UP_FAIL
} from "../action-types";

export const signUp = ({firstName,middleName,lastName,email,password})=>async dispatch=>{
    const body = JSON.stringify({firstName,middleName,lastName,email,password});
    const config = {
      headers:{
          "Content-Type":"application/json"
      }
    };
    try {
      const res = await appData.post('/signUp',body,config);
      dispatch({
          type:SIGN_UP_SUCCESS,
          payload:res.data
      })
    }catch (error) {
        console.log(error.response.data)
        dispatch({
            type:SIGN_UP_FAIL,
            payload:null
        })
    }



};

export const signIn = (email,password)=>async dispatch=>{

    const body = JSON.stringify({email,password});

    const config ={
        headers:{
            "Content-Type":"application/json"
        }
    };

    try{
      const res = await appData.post('/signIn',body,config);
      dispatch({
          type:SIGN_IN_SUCCESS,
          payload:res.data
      })
    }catch (error) {
        dispatch({
            type:SIGN_IN_FAIL,
            payload: null
        })
    }


};
