import React from 'react';
import {useTransition} from 'react-spring';
import ProductGridItem from "./productGridItem/productGridItem.component";
import './productGrid.styles.scss'


const ProductGrid= ({products}) => {

    const transitions = useTransition(products,product=> product._id,{
        from: { transform: 'translate3d(0,-40px,0)' },
        enter: { transform: 'translate3d(0,0px,0)' },
        leave: { transform: 'translate3d(0,-40px,0)' },
    });



    return (
        transitions.map(({product,animatedProps,key})=> <ProductGridItem key={key} style={animatedProps} product={product}/>)

    );
};

export default ProductGrid;

