import {ADD_TO_CART, LOAD_CART, REMOVE_FROM_CART, UNLOAD_CART} from "../action-types";
import appData from "../../api/appData";
import {body} from "express-validator";


export const addToCart = (id,item) =>async dispatch =>{
    const body = JSON.stringify({
       id,item
    });
    const config ={
        headers:{
            "Content-Type":"application/json"
        }
    };
    const res = await appData.put('/cart/add',body,config);
  dispatch({
      type:ADD_TO_CART,
      payload:res.data.cart
  })
};

export const removeFromCart = item => dispatch=>{

  dispatch({
      type: REMOVE_FROM_CART,
      payload: item
  })
};

export const unLoadCart = ()=>dispatch=>{
  dispatch({
      type:UNLOAD_CART
  })
};

export const loadCart = (email) =>async dispatch=>{
    const body = JSON.stringify({email});
    console.log(body);
    const config ={
        headers:{
            "Content-Type":"application/json"
        }
    };
    const cart = await appData.post('/cart',body,config);
    console.log(cart.data);
    dispatch({
        type:LOAD_CART,
        payload:cart.data.cart
    })
};
