import {FETCH_USER} from "../action-types";

import appData from "../../api/appData";


export const fetchUser = (email,password)=>async dispatch=>{

  const response = await appData.get(`/users/`);
  const user = response.data.find(e=>{
     return e.email === email && e.password===password
  });
  console.log(user);
  dispatch({
      type:FETCH_USER,
      payload:user
  })


};
