import React from 'react';
import './cart-item.scss';
import Img from 'react-image';
import {addToCart,removeFromCart} from "../../redux/cart/cart.actions";
import {connect} from "react-redux";

const CartItem = ({image,name,price,itemCount,addToCart,removeFromCart}) => {
    console.log(image,name,price,itemCount);
    return (
        <div className={`cart-item`}>
            <Img src={image} className={`cart-item-img`}/>
            <div className={`cart-item-info`}>
                <h3 className={`cart-item-info-name`}>{name}</h3>
                <span className={`cart-item-info-price`}>{price}</span>
                <div className={`cart-item-info-quantity`}>
                    <span>&larr;</span>
                    <span>{itemCount}</span>
                    <span>&rarr;</span>
                </div>
            </div>

            <span>remove </span>
        </div>
    );
};

export default connect(null,{addToCart,removeFromCart})(CartItem);
