import {ADD_TO_CART, LOAD_CART, REMOVE_FROM_CART, UNLOAD_CART} from "../action-types";
import {addToCart, removeFromCart} from "./cart.utils";

const initialState = {
  id:null,
  items:[],
  email:null
};

const cartReducer = (state=initialState,action)=>{
  const {type,payload} = action;
  delete state.cart;
  switch (type) {
    case ADD_TO_CART:
    case REMOVE_FROM_CART:
      return{
        ...state,
        items: payload.items
      };
    case LOAD_CART:
      return {
        ...state,
        items: payload.items,
        email: payload.email,
        id:payload._id
      };
    case UNLOAD_CART:
      return {
        ...state,
        email: null,
        items: []
      };
    default:
      return state;
  }

};

export default cartReducer;
