import React from 'react';

import './sidebar.style.scss';
import SidebarSection from "./sidebarSection/sidebarSection.component";

import { Header, Icon, Image, Menu, Segment, Sidebar } from 'semantic-ui-react';
import useBooleanKnob from "@stardust-ui/docs-components/dist/es/knobs/useBooleanKnob";

// const Sidebar = () => {
//     return <div className={`sidebar`}>
//
//         <SidebarSection/>
//     </div>
// };


const SidebarExampleSidebar = () => {
    const [visible, setVisible] = useBooleanKnob({ name: 'visible' });

    return (
        <Sidebar.Pushable as={Segment}>
            <Sidebar
                as={Menu}
                animation='overlay'
                icon='labeled'
                inverted
                onHide={() => setVisible(false)}
                vertical
                visible={visible}
                width='thin'
            >
                <Menu.Item as='a'>
                    <Icon name='home' />
                    Home
                </Menu.Item>
                <Menu.Item as='a'>
                    <Icon name='gamepad' />
                    Games
                </Menu.Item>
                <Menu.Item as='a'>
                    <Icon name='camera' />
                    Channels
                </Menu.Item>
            </Sidebar>

            <Sidebar.Pusher>
                <Segment basic>
                    <Header as='h3'>Application Content</Header>
                    <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
                </Segment>
                <button onClick={()=>setVisible(!visible)}> sidebar</button>
            </Sidebar.Pusher>
        </Sidebar.Pushable>
    )
};

export default SidebarExampleSidebar;
