import appData from "../../api/appData";
import setAuthToken from "../../utils/setAuthToken";
import {LOAD_USER, LOAD_USER_FAIL} from "../action-types";


export const loadUser = token => async dispatch=>{
    // console.log(token)
    // setAuthToken(token);
    const config = {
        headers:{
            'x-auth-token':JSON.stringify(token)
        }
    };
  try {
      const res = await appData.get('/signIn',config);
      console.log(res.data)
      dispatch({
          type:LOAD_USER,
          payload:res.data
      })
  }catch (e) {
      dispatch({
          type: LOAD_USER_FAIL
      })
  }
};
